EmployeeView = React.createClass({
  propTypes: {
    // This component gets the employee to display through a React prop.
    // We can use propTypes to indicate it is required
    employee: React.PropTypes.object.isRequired
  },

  handleChange: function(event) {
    var originalValue = this.props.employee.name.first;
    var inputValue = event.target.value;
    var inputName = event.target.name;

    if (originalValue != inputValue){
      Employees.update(this.props.employee._id, {
        $set:{ "name.first" : inputValue, "name.last" : inputValue},
      });
    }
  },

 render() {
        return ( 
<form className="form-horizontal">
<nav className="navbar navbar-default">
    <div className="navbar-header">
        <img alt="Innovate" className="img-responsive pull-left" height ="10%" width="10%" src="https://s3.amazonaws.com/itmi-web-resources/images/assessment/logo.svg"></img>
              <button type="submit" onClick={this.handleBack} className="btn btn-default pull-left">Back</button>
              <button type="submit" onClick={this.handleChange} className="btn btn-default pull-right">Save</button>
    </div>
</nav>
      <div className='row'>
            <div className='col-sm-12'>
              <img src={this.props.employee.picture.toString()} alt={this.props.employee.name.first + " "+ this.props.employee.name.last} className="img-responsive img-circle"/>                
            </div>
      </div>
  </form>
    );
  }
});
