    // We can use propTypes to indicate it is required
// Employee component - represents a single employee 
EmployeeList = React.createClass({
  propTypes: {
    // This component gets the task to display through a React prop.
    // We can use propTypes to indicate it is required
    employee: React.PropTypes.object.isRequired
  },


  renderEmployeeView() {
      return <EmployeeView key={employee._id} employee={employee}/>;
  },

 render() {
        return (
      <li className="list-group-item"  onClick={this.renderEmployeeView}>
                    <div className="col-xs-12 col-sm-2">
                        <img src={this.props.employee.picture.toString()} alt={this.props.employee.name.first + " "+ this.props.employee.name.last} className="img-responsive img-circle"/>
                    </div>
                    <div className="col-xs-12 col-sm-10">
                        <h3><span className="name">{this.props.employee.name.first} {this.props.employee.name.last}</span></h3>
                    </div>
                    <div className="col-xs-12 col-sm-10">
                        <h4><span className="text">{this.props.employee.department}</span></h4>
                    </div>
                    <div className="clearfix"></div>
      </li>
    );
  }
});
