EmployeeView = React.createClass({
  propTypes: {
    // This component gets the employee to display through a React prop.
    // We can use propTypes to indicate it is required
    employee: React.PropTypes.object.isRequired
  },

  handleChange: function(event) {
    var originalValue = this.props.employee.name.first;
    var inputValue = event.target.value;
    var inputName = event.target.name;

    if (originalValue != inputValue){
      Employees.update(this.props.employee._id, {
        $set:{ "name.first" : inputValue, "name.last" : inputValue},
      });
    }
  },

 render() {
        return ( 
          <form className="form-horizontal">

          <div className="form-group">
                    <div className="col-md-8">
            <button id="button1id" name="button1id" className="btn btn-success">Good Button</button>
            <img src="https://s3.amazonaws.com/itmi-web-resources/images/assessment/logo.svg" alt="Inovate Logo" className="img-responsive img-circle"/>
            <button id="button2id" name="button2id" className="btn btn-danger">Scary Button</button>
          </div>
              <div className="col-xs-12 col-sm-2">
                <img src={this.props.employee.picture.toString()} alt={this.props.employee.name.first + " "+ this.props.employee.name.last} className="img-responsive img-circle"/>
              </div>
              <fieldset>  
              <label className="col-md-4 control-label" for="firstName">First Name</label>  
              <div className="col-md-8">
                <input id="firstName" 
                ref="firstName" 
                name="first" 
                type="text" 
                className="form-control input-md"
                onBlur={this.handleChange}
                defaultValue={this.props.employee.name.first}/>   
              </div>
              <label className="col-md-4 control-label" for="lastName">Last Name</label>  
              <div className="col-md-8">
                <input id="lastName" 
                ref="lastName" 
                name="last" 
                type="text" 
                className="form-control input-md"
                onBlur={this.handleChange}
                defaultValue={this.props.employee.name.last}/>   
              </div>
              <label className="col-md-4 control-label" for="email">Email</label>  
              <div className="col-md-8">
                <input id="email" 
                ref="email" 
                name="email" 
                type="email" 
                className="form-control input-md"
                onBlur={this.handleChange}
                defaultValue={this.props.employee.email}/>   
              </div>
              <label className="col-md-4 control-label" for="phone">Phone</label>  
              <div className="col-md-8">
                <input id="phone" 
                ref="phone" 
                name="phone" 
                type="phone" 
                className="form-control input-md"
                defaultValue={this.props.employee.phone}/>   
              </div>
              </fieldset>
          </div>
          </form>
    );
  }
});
